const cardWrapper=document.querySelector(".card-wrapper");async function fetchAPI(){const a=await fetch("https://api.punkapi.com/v2/beers?page=1&per_page=9"),t=await a.json();console.log(t),getData(t)}function getData(a){let t="";a.map((a=>{t+=`\n  <div class="col-md-4">\n  <div class="card text-center">\n<div class="card-body bg-primary text-white">\n<img class="card-img-top" src=${a.image_url} alt="">\n<p>  Some text</p>\n</div>\n<div class="card-footer">\n<h5>\n<a href="#" class="pt-3"> Add to Card</a>\n</h5>\n</div>\n  </div>\n</div>\n  \n  `})),cardWrapper.innerHTML=t}fetchAPI();