const cardWrapper=document.querySelector(".card-wrapper");async function fetchAPI(){const a=await fetch("https://api.punkapi.com/v2/beers?page=1&per_page=9"),n=await a.json();console.log(n),getData(n)}function getData(a){let n="";a.map((a=>{n+=`\n    <div class="col-md-4">\n    <div class="card text-center cards">\n      <div class="card-body card-main">\n        <img class="card-img-top" src=${a.image_url} alt="image" />\n        <p>$${a.abv}</p>\n      </div>\n      <div class="card-footer card-bottom">\n        <h5>\n       <button class="w-100">Add to Card</button>\n        </h5>\n      </div>\n    </div>\n  </div>\n  \n  `})),cardWrapper.innerHTML=n}fetchAPI();