const cardWrapper=document.querySelector(".card-wrapper"),modal=document.getElementById("modal"),modalStart=document.getElementById("modalClick"),modalClose=document.querySelector(".close-btn");async function fetchAPI(){const a=await fetch("https://api.punkapi.com/v2/beers?page=1&per_page=9"),e=await a.json();console.log(e),getData(e)}function getData(a){let e="";a.map((a=>{e+=`\n    <div class="col-md-4">\n    <div class="card text-center border-0 cards">\n      <div class="card-body card-main">\n        <img class="img" src=${a.image_url} alt="image" />\n        <p>$${a.abv}</p>\n        <div class="logo">\n          <i class="fa-solid fa-link fa-3x"></i>\n        </div>\n      </div>\n      <div class="card-footer card-bottom">\n        <h5>\n       <button class="w-100">Add to Card</button>\n        </h5>\n      </div>\n    </div>\n  </div>\n  \n  `})),cardWrapper.innerHTML=e}function openModal(){modal.style.display="block"}function closeModal(){modal.style.display="none"}modalStart.addEventListener("click",openModal),modalClose.addEventListener("click",closeModal),fetchAPI();